<?php\n\nfunction cidr_match($ip, $cidr) {\n    list($subnet, $mask) = explode('/', $cidr);\n    \n    $ipInt = ip2long($ip);\n    $subnetInt = ip2long($subnet);\n    \n    // Create a binary number with '1's in the first `$mask` bits and '0's elsewhere\n    $maskBin = -1 << (32 - $mask);\n\n    // Check if the IP is within the subnet range\n    return ($ipInt & $maskBin) === ($subnetInt & $maskBin);\n}\n\n$ips = array('10.2.1.100', '10.2.1.101', '10.5.1.100', '1.2.3.4');\n\nforeach ($ips as $IP) {\n    if (cidr_match($IP, '10.2.0.0/16') == true) {\n        print "you're in the 10.2 subnet\n"; \n    }\n}\n?>