amespace MyPackage;\n\nclass ClassA {\n    protected $protectedProperty = 'I am protected';\n    private $privateProperty = 'I am private';\n\n    public function getProtectedProperty() {\n        return $this->protectedProperty;\n    }\n\n    public function getPrivateProperty() {\n        return $this->privateProperty;\n    }\n}\n\nclass ClassB {\n    public function useClassAProps() {\n        $objA = new ClassA();\n        echo $objA->getProtectedProperty(); // Accessible\n        echo $objA->getPrivateProperty();   // Also accessible within the same namespace\n    }\n}