// Function to get filtered directory list\nfunction getFilteredDirectoryList($dir) {\n    // Get all files and directories from $dir\n    $files = scandir($dir);\n    \n    // Filter out '.' '..', and '.svn'\n    $filteredFiles = array_filter($files, function ($file) use ($dir) {\n        return !in_array($file, ['.', '..']) && strpos($file, '.svn') === false;\n    });\n    \n    return array_values($filteredFiles); // Re-index the array\n}\n\n// Example usage\n$directoryList = getFilteredDirectoryList('/path/to/your/directory');\nprint_r($directoryList);