<?php\n\nfunction getCacheFileName() {\n    // Use the full URL as part of the cache file name\n    return __DIR__ . '/cache/' . md5($_SERVER['REQUEST_URI']) . '.html';\n}\n\nfunction isCached($filePath, $duration) {\n    if (file_exists($filePath)) {\n        // Check if the cache is recent enough\n        if ((time() - filemtime($filePath) < $duration)) {\n            return true;\n        }\n    }\n    return false;\n}\n\ncacheTime = 3600; // Cache duration in seconds (1 hour)\n\n$cacheFile = getCacheFileName();\n\nif (!isCached($cacheFile, cacheTime)) {\n    ob_start(); // Start buffering output\n\n    // Your site logic here\n    echo "<html><body><h1>Hello World!</h1></body></html>";\n\n    $content = ob_get_clean(); // Get the buffered content\n\n    file_put_contents($cacheFile, $content); // Cache it\n} else {\n    readfile($cacheFile); // Serve cached content\n    exit;\n}\n\n?>