use Zend\Search\Lucene;\n\n// Create a new index in the 'data/index' directory\n$index = Lucene::create('/path/to/data/index');\n\n// Create a document object\n$doc = new Lucene\Document();\n\n// Add fields to the document\n$doc->addField(Lucene\Field\Text::create('title', 'Example Document Title'));\n$doc->addField(Lucene\Field\UnStored::create('contents', 'This is an example content of the document.'));\n\n// Add the document to the index\n$index->addDocument($doc);\n\n// Commit changes to the index\n$index->commit();