<?php\nfunction getZipsInRadius($lat, $lon, $radius) {\n    $sql = "\n        SELECT ZipCode FROM zip_codes \n        WHERE 3958 * acos(\n            cos(radians(:latitude)) \n            * cos(radians(Latitude)) \n            * cos(radians(Longitude) - radians(:longitude)) \n            + sin(radians(:latitude)) \n            * sin(radians(Latitude))\n        ) <= :radius\n    ";\n\n    $pdo = new PDO('mysql:host=localhost;dbname=your_database', 'username', 'password');\n    $stmt = $pdo->prepare($sql);\n    $stmt->execute([':latitude' => $lat, ':longitude' => $lon, ':radius' => $radius]);\n\n    return $stmt->fetchAll(PDO::FETCH_COLUMN);\n}\n\n$zipCodesInRadius = getZipsInRadius(40.7128, -74.0060, 10); // Example coordinates and radius\nprint_r($zipCodesInRadius);\n?>