class Actividad extends AppModel {\n    var $name = 'Actividad';\n    var $validate = array(\n        'maxfield' => array(\n            'rule' => array('chkValue'),\n            'message' => 'Max field must be greater than or equal to min field'\n        )\n    );\n\n    function chkValue($data) {\n        // Access the minfield value from the model data\n        if (isset($this->data[$this->alias]['minfield'])) {\n            return $data['maxfield'] >= $this->data[$this->alias]['minfield'];\n        }\n        return false;\n    }\n}