function customErrorHandler($level, $message, $file, $line, $context) {\n    echo "Sorry, an error has occurred on line $line.<br />";\n    echo "The error says: $message.<br />";\n    return true; // Indicate that the error is handled\n}\n\nfunction shutdownHandler() {\n    $error = error_get_last();\n    if ($error !== null && in_array($error['type'], [E_ERROR, E_PARSE, E_COMPILE_ERROR, E_CORE_ERROR])) {\n        echo "Fatal error encountered: {$error['message']} on line {$error['line']}";\n    }\n}\n\nset_error_handler("customErrorHandler");\nregister_shutdown_function("shutdownHandler");\n\nimAFakeFunction();