<?php\nfunction parseSql($sql) {\n    // Convert to uppercase to simplify pattern matching\n    $sql = strtoupper($sql);\n    \n    // Remove commas and other punctuation marks except WHERE clause content\n    $sql = preg_replace('/,\s*/', ' ', $sql); // remove commas\n    $sql = preg_replace('/(?<=\W)WHERE(\s+)\K[^]+/', '', $sql, 1); // preserve WHERE clause values\n    \n    // Split the SQL string into components by whitespace and keywords\n    $components = preg_split('/\s+(?=(SELECT|FROM|WHERE)\b)/', $sql);\n    \n    return $components;\n}\n\n$sqlString = "SELECT * FROM tbl WHERE user_id > 50";\n$arr = parseSql($sqlString);\n\nprint_r($arr);\n?>