function mymodule_create_node_from_cron($title, $body, $type = 'article', $language = LANGUAGE_NONE) {\n  // Create a new node object.\n  $node = new stdClass();\n\n  // Set the basic properties of the node.\n  $node->nid = NULL; // New node means no nid.\n  $node->vid = NULL; // No vid initially.\n  $node->type = $type;\n  $node->language = $language;\n\n  // Set the title and body values.\n  $node->title = $title;\n  $node->body[$node->language][0]['value']   = $body;\n  $node->body[$node->language][0]['summary'] = text_summary($body);\n  $node->body[$node->language][0]['format']  = 'filtered_html'; // Change format as needed.\n\n  // Set the node status to published.\n  $node->status       = NODE_PUBLISHED;\n  $node->promote      = FALSE; // Optional: Set this value if not promoting node to front page.\n  $node->sticky       = FALSE;\n  $node->created      = REQUEST_TIME; // Use current request time.\n  $node->changed      = REQUEST_TIME;\n\n  // Set the author uid. Optionally set this to another uid if needed.\n  $node->uid          = 1;\n\n  // Optionally set other fields here. For example, taxonomy terms or custom CCK fields.\n\n  // Save the node object.\n  node_save($node);\n\n  return $node;\n}