<?php\n// Parse current query string into an array\n$queryParams = [];\nparse_str($_SERVER['QUERY_STRING'], $queryParams);\n\n// Function to generate URL with modified query params\nfunction generateUrlWithModifiedQueryParam($key, $value) {\n    global $queryParams;\n    // Modify the specific parameter\n    $queryParams[$key] = $value;\n\n    // Generate the new query string\n    return '?' . http_build_query($queryParams);\n}\n\n// Example usage: Change page to 3\n$newPageUrl = generateUrlWithModifiedQueryParam('page', 3);\n\n// Example usage: Change sort method to 'title'\n$newSortUrl = generateUrlWithModifiedQueryParam('sort', 'title');\n\necho "Link to Page 3: <a href=\"$newPageUrl\">Go to Page 3</a><br>";\necho "Link to Sort by Title: <a href=\"$newSortUrl\">Sort by Title</a>";\n?>