<?php\n   // Fetch recent posts from WordPress blog\n   $url = 'http://yourdomain.com/blog/wp-json/wp/v2/posts';\n   $ch = curl_init();\n   curl_setopt($ch, CURLOPT_URL, $url);\n   curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n   $output = curl_exec($ch);\n   curl_close($ch);\n   \n   if ($output) {\n       $posts = json_decode($output);\n       foreach ($posts as $post) {\n           echo "<h2>" . htmlspecialchars($post->title->rendered) . "</h2>";\n           echo "<div>" . htmlspecialchars($post->excerpt->rendered) . "</div>";\n           echo "<a href='http://yourdomain.com/blog/?p=" . $post->id . "'>Read more</a><br>";\n       }\n   } else {\n       echo 'No posts found';\n   }\n   ?>