<?php\n$mysqli = new mysqli("localhost", "username", "password", "dbname");\n\nif ($mysqli->connect_error) {\n    die('Connect Error (' . $mysqli->connect_errno . ') ' . $mysqli->connect_error);\n}\n\n$stmt = $mysqli->prepare("INSERT INTO articles (title, description, ctext, chtml) VALUES (?, ?, ?, ?)");\n$stmt->bind_param("ssss", $title, $description, $text, $html);\n\n// Assuming $title, $description, $text, and $html are already set\n\nif ($stmt->execute()) {\n    echo "New record created successfully";\n} else {\n    echo "Error: " . $stmt->error;\n}\n\n$stmt->close();\n$mysqli->close();\n?>