use TYPO3\CMS\Core\Utility\GeneralUtility;\nuse TYPO3\CMS\Frontend\ContentObject\ContentObjectRenderer;\n\n// Assuming $contentElementUids is an array of UIDs from tt_content\n$contentElementUids = [123, 456]; // Replace with your actual UIDs\n\n$contentObjectsRendered = [];\nforeach ($contentElementUids as $uid) {\n    $cObj = GeneralUtility::makeInstance(ContentObjectRenderer::class);\n    \n    // Set the current UID and PID if necessary\n    $cObj->start(['uid' => $uid], 'tt_content');\n    \n    // Get the content element rendered via TEXT_CONTENT object\n    $contentObjectsRendered[] = $cObj->cObjGetSingle(\n        'RECORDS',\n        [\n            'tables' => 'tt_content',\n            'source' => $uid,\n            'conf.' => [\n                'tt_content.' => [\n                    'renderingOption.' => [\n                        'allowCaching' => 1\n                    ]\n                ],\n                'stdWrap.override.if.' => [\n                    'isTrue' => '{$styles.content.get.stdWrap.override}'\n                ]\n            ]\n        ]\n    );\n}\n\n// Output the rendered content elements\necho implode("\n", $contentObjectsRendered);