$username = $_POST["username"];\n$password = $_POST["password"];\n\n$url = "http://search.twitter.com/search.atom?q=golf&show_user=true&rpp=100";\n$search = file_get_contents($url);\n\n$regex_name = '/\<name\>(.+?) \(/';\npreg_match_all($regex_name, $search, $user);\nfor ($i = 0; $user[1][$i]; $i++) {\n    $follow = $user[1][$i];\n    $credentials = $username . ':' . $password; // Concatenate username and password\n    $url = "http://twitter.com/friendships/create/" . $follow . ".xml";\n    $ch = curl_init();\n    curl_setopt($ch, CURLOPT_URL, $url);\n    curl_setopt($ch, CURLOPT_POST, 1);\n    curl_setopt($ch, CURLOPT_USERPWD, $credentials); // Use the concatenated string\n    $result = curl_exec($ch);\n    curl_close($ch);\n}