<?php\n\n// Your API Key from exchangerate-API\n$apiKey = 'YOUR_API_KEY_HERE';\n\n// Base URL of the API endpoint\n$url = "https://v6.exchangerate-api.com/v6/$apiKey/latest/AUD";\n\n// Initialize a cURL session\n$ch = curl_init($url);\n\n// Set options for cURL transfer\ncurl_setopt_array($ch, [\n    CURLOPT_RETURNTRANSFER => true,\n]);\n\n// Execute the request and store the result\n$response = curl_exec($ch);\n\n// Close the cURL session\ncurl_close($ch);\n\n// Decode JSON response to an associative array\n$data = json_decode($response, true);\n\nif ($data['result'] === 'success') {\n    // Access exchange rate for USD\n    $usdRate = $data['conversion_rates']['USD'];\n    // Access exchange rate for EUR\n    $eurRate = $data['conversion_rates']['EUR'];\n\n    echo "1 AUD is $usdRate USD and $eurRate EUR";\n} else {\n    echo "Error fetching data: " . $data['error-type'];\n}\n\n?>