public function getTotal($tax)\n{\n    $total = 0.00;\n\n    $callback =\n        function ($quantity, $product) use ($tax, &$total)\n        {\n            $pricePerItem = constant(__CLASS__ . "::PRICE_" .\n                strtoupper($product));\n            $total += ($pricePerItem * $quantity) * ($tax + 1.0);\n        };\n\n    array_walk($this->products, $callback);\n    return round($total, 2);\n}