<?php\n$ch = curl_init();\n\n// Set URL and other appropriate options\n$url = "http://foller.me/tmp/postdump.php";\ncurl_setopt($ch, CURLOPT_URL, $url);\ncurl_setopt($ch, CURLOPT_POST, 1);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query(array('something' => 'somewhere')));\n\n// Receive server response ...\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n// Executing the request\n$response = curl_exec($ch);\n\nif(curl_errno($ch)){\n    echo 'Curl error: ' . curl_error($ch);\n}\n\necho $response;\n\n// Closing the session and freeing all resources used by the cURL session\ncurl_close($ch);\n?>