<?php\nheader("Content-Type: application/json");\n\n$response = array();\n\n// Check for POST request and required parameters\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    $data = json_decode(file_get_contents('php://input'), true);\n\n    if (isset($data['reference'], $data['amount'], $data['status'], $data['description'])) {\n        // Process the data\n        // For example, you can update your database here with the status of transaction\n\n        $response['success'] = true;\n        $response['message'] = "Transaction updated successfully";\n    } else {\n        $response['success'] = false;\n        $response['message'] = "Missing required parameters";\n    }\n} else {\n    $response['success'] = false;\n    $response['message'] = "Invalid request method or data format";\n}\n\necho json_encode($response);\n?>