// application/models/ProjectCategories.php\nclass Default_Model_ProjectCategories extends Zend_Db_Table_Abstract {\n    protected $_name = 'categories';\n    protected $_dependentTables = array('Default_Model_Projects');\n\n    public function findProjects($categoryId) {\n        $projectsTable = new Default_Model_Projects();\n        return $projectsTable->fetchAll(\n            $projectsTable->select()->where('cid = ?', $categoryId)\n        );\n    }\n}\n\n// application/models/Projects.php\nclass Default_Model_Projects extends Zend_Db_Table_Abstract {\n    protected $_name = 'projects';\n\n    protected $_referenceMap    = array(\n        'Category' => array(\n            'columns'           => 'cid',\n            'refTableClass'     => 'Default_Model_ProjectCategories', // Ensure this is fully qualified\n            'refColumns'        => 'id',\n            'onUpdate'          => self::CASCADE,\n            'onDelete'          => self::CASCADE,\n        )\n    );\n}