<?php\n\nfunction isSiteAvailableWithCurl($url) {\n    $ch = curl_init($url);\n    curl_setopt($ch, CURLOPT_NOBODY, true); // Head request only\n    curl_exec($ch);\n    $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);\n    curl_close($ch);\n\n    // Check HTTP response code is 200 (OK)\n    return $httpCode === 200;\n}\n\n$url = "https://www.twitter.com";\nif (isSiteAvailableWithCurl($url)) {\n    echo "true"; // Site is available\n} else {\n    echo "false"; // Site is not available\n}