<?php\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "myDB";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$sql = "SELECT * FROM myTable";\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    $i = 0; // Initialize row counter\n    echo "<pre>"; // Just for formatting in console or browser\n\n    while($row = $result->fetch_assoc()) {\n        echo "id: " . $row["id"]. " - Name: " . $row["name"] . "\n"; // Modify this line according to your table's columns\n\n        $i++; // Increment row counter\n        if ($i % 5 == 0) { // Check if five rows have been printed\n            echo "<br />"; // Print a new line after every five rows\n        }\n    }\n\n    echo "</pre>";\n} else {\n    echo "0 results";\n}\n\n$conn->close();\n?>