use Laminas\Http\Request;\nuse Laminas\File\Transfer\Adapter\Http as HttpAdapter;\n\n$request = new Request();\n$httpAdapter = new HttpAdapter();\n\nif ($request->isPost()) {\n    // Check if files were uploaded\n    if (!empty($httpAdapter->getFileInfo())) {\n        try {\n            $files = $httpAdapter->getFiles();\n            foreach ($files as $file) {\n                if (!$httpAdapter->receive($file['name'])) {\n                    throw new \Exception("File upload failed: " . implode('; ', $httpAdapter->getMessages()));\n                }\n                echo "File uploaded successfully: " . $file['name'];\n            }\n        } catch (\Exception $e) {\n            echo 'Error occurred: ' . $e->getMessage();\n        }\n    } else {\n        echo 'No files were uploaded.';\n    }\n} else {\n    echo 'No POST request received.';\n}