<?php\n\n// Define a simple function\nfunction myCallbackFunction() {\n    echo "Hello from callback function!\n";\n}\n\n// Define a class with a method\nclass MyClass {\n    public static function myStaticMethod($name) {\n        echo "Hello $name from a static method\n";\n    }\n    \n    public function myNormalMethod($greeting, $name) {\n        echo "$greeting, $name from an instance method\n";\n    }\n}\n\n// Using a closure\n$closure = function($greeting, $name) {\n    echo "$greeting, $name from a closure!\n";\n};\n\n// Using the callback types\n\n// Calling with string\ncall_user_func('myCallbackFunction');\n\n// Calling with array using static method\ncall_user_func(['MyClass', 'myStaticMethod'], 'World');\n\n// Create an instance of MyClass and call its method\n$instance = new MyClass();\ncall_user_func([$instance, 'myNormalMethod'], 'Hello', 'User');\n\n// Using a closure directly\n$closure('Hi', 'John');\n?>