// Start with the existing GET parameters\n$currentGetParams = $_GET;\n\n// Add any new parameters you want to include\n$newParams = ['newVar' => 2];\n$currentGetParams = array_merge($currentGetParams, $newParams);\n\n// Build the query string\n$queryString = http_build_query($currentGetParams);\n\n// Output your link with all the GET variables included\necho "<a href='?" . htmlspecialchars($queryString) . "'>Link</a>";