function sortLinksByCategoryAndDate($links) {\n    $sortedLinks = [];\n\n    // First, organize links by category\n    foreach ($links as $link) {\n        if (!isset($sortedLinks[$link['category_name']])) {\n            $sortedLinks[$link['category_name']] = [];\n        }\n        $sortedLinks[$link['category_name']][] = [\n            'link_name' => $link['link_name'],\n            'link_updated' => $link['link_updated']\n        ];\n    }\n\n    // Sort each category by link_updated\n    foreach ($sortedLinks as &$linksInCategory) {\n        usort($linksInCategory, function($a, $b) {\n            return strcmp($b['link_updated'], $a['link_updated']);\n        });\n\n        // Keep only the top 5 links in each category\n        $linksInCategory = array_slice($linksInCategory, 0, 5);\n    }\n\n    return $sortedLinks;\n}\n\n// Example usage:\n$links = [\n    ["link_id" => "20", "link_url" => "http://url.of/website", "link_name" => "Website title", "link_target" => "", "link_description" => "", "link_updated" => "2009-05-24 16:51:04", "taxonomy_id" => "36", "term_id" => "34", "category_name" => "First category"],\n    ["link_id" => "21", "link_url" => "http://url.of/website2", "link_name" => "Newest link", "link_target" => "", "link_description" => "", "link_updated" => "2009-05-24 16:51:24", "taxonomy_id" => "36", "term_id" => "35", "category_name" => "First category"],\n    ["link_id" => "22", "link_url" => "http://url.of/website3", "link_name" => "Older link", "link_target" => "", "link_description" => "", "link_updated" => "2009-05-20 05:32:56", "taxonomy_id" => "37", "term_id" => "38", "category_name" => "Second category"]\n];\n\n$result = sortLinksByCategoryAndDate($links);\nprint_r($result);