<?php\nfunction isMobileSafari() {\n    // Get the user agent string\n    $userAgent = $_SERVER['HTTP_USER_AGENT'];\n\n    // Check for Mobile Safari specific conditions\n    return stripos($userAgent, 'iPhone') !== false &&\n           stripos($userAgent, 'AppleWebKit') !== false &&\n           stripos($userAgent, 'Mobile/') !== false;\n}\n\nif (isMobileSafari()) {\n    // Include the mobile.css file\n    echo '<link rel="stylesheet" type="text/css" href="mobile.css">';\n\n    // Serve smaller thumbnails\n    $thumbnailSize = 'small';\n} else {\n    // Include the regular CSS file\n    echo '<link rel="stylesheet" type="text/css" href="style.css">';\n\n    // Serve normal sized thumbnails\n    $thumbnailSize = 'normal';\n}\n\n// Assuming you have image names like thumbnail_small.jpg and thumbnail_normal.jpg\n$imageName = "thumbnail_$thumbnailSize.jpg";\necho "<img src='$imageName' alt='Thumbnail'>";\n?>