// index.php or main entry point for admin section\nsession_start();\nrequire_once 'config.php';\nrequire_once 'authentication.php';\n\nif (!isAuthenticated()) {\n    header('Location: login.php');\n    exit;\n}\n\ninclude 'admin-header.php'; // Include header for all pages\n\n$page = isset($_GET['page']) ? $_GET['page'] : 'dashboard';\nswitch ($page) {\n    case 'dashboard':\n        include 'admin-dashboard.php';\n        break;\n    case 'users':\n        include 'admin-users.php';\n        break;\n    default:\n        include 'admin-error.php'; // Handle incorrect page requests\n}\n\ninclude 'admin-footer.php'; // Include footer for all pages\n\n// Function to check authentication status\nfunction isAuthenticated() {\n    return isset($_SESSION['user']) && $_SESSION['user']['role'] === 'admin';\n}