$tablename = 'users'; // Example table name\n$id = 1; // Example ID\n\n// Using printf to replace placeholders, but not recommended directly in SQL queries due to security risks.\n$sqlTemplate = "SELECT COUNT(*) AS test FROM %s WHERE id = %d AND tmp_mail <> ''";\n$formattedSql = sprintf($sqlTemplate, $tablename, $id);\n\n// Recommended: Use prepared statements\n$mysqli = new mysqli("localhost", "user", "password", "database");\nif ($mysqli->connect_error) { die($mysqli->connect_error); }\n\n$stmt = $mysqli->prepare("SELECT COUNT(*) AS test FROM ? WHERE id = ? AND tmp_mail <> ''");\n// Note: Prepared statements do not support changing table names with placeholders.\n// Bind variables to the prepared statement as parameters\n$table_safe = $tablename; // Ensure this is safe, e.g., from a whitelist of allowed tables\n$stmt->bind_param("si", $table_safe, intval($id)); // "s" for string, "i" for integer\n$stmt->execute();\n$result = $stmt->get_result();\n$row = $result->fetch_assoc();\n\necho $row['test'];\n$mysqli->close();