<?php\nsession_start();\n\n// Validate the input is JSON\n$json_data = file_get_contents('php://input');\n$data = json_decode($json_data, true);\n\nif (json_last_error() !== JSON_ERROR_NONE) {\n    header("HTTP/1.0 400 Bad Request");\n    echo '{"error": "Invalid JSON"}';\n    exit;\n}\n\n$session_id = session_id();\n\ntry {\n    // Open SQLite database\n    $db = new PDO('sqlite:q_and_a_storage.db');\n    $db->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Prepare a statement and bind parameters to avoid SQL injection\n    $stmt = $db->prepare("INSERT OR REPLACE INTO q_and_a_storage (php_session_id, json_storage) VALUES (:session_id, :json_data)");\n    $stmt->bindParam(':session_id', $session_id);\n    $stmt->bindParam(':json_data', $json_data);\n\n    // Execute statement\n    $stmt->execute();\n} catch (PDOException $e) {\n    header("HTTP/1.0 500 Internal Server Error");\n    echo '{"error": "Database error"}';\n    exit;\n}\n\necho '{"success": true}';\n?>