<?php\n// Assuming you have already set up your database connection\n$conn = new mysqli('hostname', 'username', 'password', 'database');\n\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$sql = "SELECT t.TagID, t.Title, COUNT(it.ItemID) AS ItemCount \n        FROM Tag t \n        JOIN ItemTag it ON t.TagID = it.TagID \n        GROUP BY t.TagID, t.Title";\n\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    while($row = $result->fetch_assoc()) {\n        echo "Tag: " . htmlspecialchars($row['Title']) . " has " . $row['ItemCount'] . " items.<br>";\n    }\n} else {\n    echo "No tags found.";\n}\n\n$conn->close();\n?>