class Bootstrap extends Zend_Application_Bootstrap_Bootstrap {\n    protected function _initAutoload() {\n        $autoloader = new Zend_Application_Module_Autoloader(array(\n            'namespace' => '',\n            'basePath'  => APPLICATION_PATH,\n        ));\n\n        // Register Doctrine autoloader\n        require_once(APPLICATION_PATH . '/path/to/doctrine/Doctrine.php');\n        Doctrine_Manager::getInstance()->setAttribute(Doctrine_Core::ATTR_AUTOLOAD_TABLE_CLASSES, true);\n        spl_autoload_register(array('Doctrine', 'autoload'));\n\n        return $autoloader;\n    }\n\n    protected function _initDoctrinConnection() {\n        $options = $this->getOptions();\n        try {\n            $doctrineOptions = $options['doctrine'];\n            Doctrine_Manager::getInstance()->openConnection($doctrineOptions['dsn'], 'default');\n        } catch (Exception $e) {\n            die('Doctrine connection failed: ' . $e->getMessage());\n        }\n    }\n\n    protected function _initModelLoader() {\n        // Register a custom model loader if your models do not follow standard conventions\n        $front = Zend_Controller_Front::getInstance();\n        $modelPath = APPLICATION_PATH . '/models';\n        set_include_path(get_include_path() . PATH_SEPARATOR . $modelPath);\n    }\n}