// Using include\n$start = microtime(true);\ninclude('somefile.php'); // Assume somefile.php contains predefined data\n$end = microtime(true);\necho "File inclusion: " . ($end - $start) . " seconds\n";\n\n// Database query\n$start = microtime(true);\n$conn = new mysqli("localhost", "username", "password", "database");\n$result = $conn->query("SELECT * FROM table_name LIMIT 3000");\n$data = $result->fetch_all();\n$conn->close();\n$end = microtime(true);\necho "Database query: " . ($end - $start) . " seconds\n";