public function indexAction() {\n    // Create the form instance\n    $form = new Application_Form_Login(); // assuming you have a Login form\n\n    // Pass the form to the view or directly to the partial\n    $this->view->form = $form;\n\n    // Optionally, if you want to pass it directly to the partial in your layout:\n    echo $this->_helper->layout()->getView()->partial('_header.phtml', array('form' => $form));\n}