<?php\n// Assuming $pdo is your PDO database connection object\n\n// Get and sanitize input parameters from the query string\n$startDate = isset($_GET['date']) ? $_GET['date'] : null;\n$category = isset($_GET['category']) ? $_GET['category'] : null;\n\n$queryParts = ["SELECT * FROM events WHERE 1=1"];\n$params = [];\n\nif ($startDate) {\n    $queryParts[] = "AND event_date >= ?";\n    $params[] = $startDate;\n}\n\nif ($category) {\n    $queryParts[] = "AND category = ?";\n    $params[] = $category;\n}\n\n$queryString = implode(" ", $queryParts);\n\ntry {\n    // Prepare the SQL statement\n    $stmt = $pdo->prepare($queryString);\n    \n    // Execute with parameters\n    $stmt->execute($params);\n    \n    // Fetch results\n    $events = $stmt->fetchAll(PDO::FETCH_ASSOC);\n    \n    foreach ($events as $event) {\n        echo "Event: " . htmlspecialchars($event['name']) . "<br>";\n    }\n} catch (PDOException $e) {\n    error_log("Database Error: " . $e->getMessage());\n    echo "An error occurred while fetching events.";\n}\n?>