<?php\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    $data = json_decode(file_get_contents('php://input'), true);\n    \n    if (isset($data['dateTime'], $data['timezoneOffset'])) {\n        // Parse the ISO date string to a DateTime object\n        $userDateTime = new DateTime($data['dateTime']);\n        \n        // Create a timezone based on the offset\n        $offsetZone = "Etc/GMT" . ($data['timezoneOffset'] < 0 ? '+' : '-') . abs($data['timezoneOffset']) / 3600;\n        $serverTimeZone = new DateTimeZone('UTC');\n        $userDateTime->setTimezone(new DateTimeZone($offsetZone));\n        \n        // Convert to server's timezone if necessary\n        $localDateTime = new DateTime('now', $serverTimeZone);\n        $utcDateTime = clone $userDateTime;\n        $utcDateTime->setTimezone($serverTimeZone);\n        $timeDifferenceInSeconds = $userDateTime->format('U') - $utcDateTime->format('U');\n        \n        // Store or use this information as needed\n        echo "User's local time is: " . $userDateTime->format('Y-m-d H:i:s') . "\n";\n        echo "Server's local equivalent time: " . $localDateTime->modify("+$timeDifferenceInSeconds seconds")->format('Y-m-d H:i:s');\n    }\n}