function buildLookupTable() {\n    $lookup = [];\n    for ($i = 0; $i < 64; $i++) {\n        $x = intdiv($i, 8);\n        $y = $i % 8;\n        $lookup[$i] = [$x, $y];\n    }\n    return $lookup;\n}\n\n$lookupTable = buildLookupTable();\n\nfunction int2coord_fast($i, $lookup) {\n    return $lookup[$i];\n}\n\n// Usage\n$x = 5;\n$y = 3;\n$index = coord2int($x, $y);\nlist($xConverted, $yConverted) = int2coord_fast($index, $lookupTable);\n\necho "Coordinates: ($xConverted, $yConverted)\n"; // Should output (5, 3)