class ResourceHandler {\n    private $resource;\n\n    public function __construct($resource) {\n        $this->resource = $resource;\n        echo "Resource acquired: " . $resource . PHP_EOL;\n    }\n\n    public function release() {\n        if ($this->resource !== null) {\n            echo "Releasing resource manually: " . $this->resource . PHP_EOL;\n            // Logic to release the resource\n            $this->resource = null;\n        }\n    }\n\n    public function __destruct() {\n        if ($this->resource !== null) {\n            echo "Destructor called, releasing resource: " . $this->resource . PHP_EOL;\n            // Logic to release the resource\n            $this->resource = null;\n        }\n    }\n}\n\n$handler = new ResourceHandler("Database Connection");