<?php\n\nfunction packRatings($ratings) {\n    // Each rating is stored in 3 bits\n    $packedValue = 0;\n    foreach ($ratings as $index => $rating) {\n        // Shift the current packed value left by 3 bits to make space for the new rating\n        $packedValue <<= 3;\n        // Add the new rating, assuming ratings are between 1 and 5\n        $packedValue |= ($rating & 7); // Use bitmask to ensure only last 3 bits are set\n    }\n    return $packedValue;\n}\n\nfunction unpackRatings($packedValue) {\n    $ratings = [];\n    for ($i = 0; $i < 5; $i++) {\n        // Extract the lowest 3 bits which contain the rating\n        $rating = $packedValue & 7;\n        // Add to the ratings array\n        $ratings[] = $rating;\n        // Shift right by 3 bits to process the next set of rating bits\n        $packedValue >>= 3;\n    }\n    return array_reverse($ratings);\n}\n\n// Example usage:\n$ratings = [5, 1, 4, 2, 3]; // User's ratings for each question\n$packed = packRatings($ratings); // Pack the ratings into a single integer\n$unpacked = unpackRatings($packed); // Unpack to retrieve original ratings\n\necho "Packed: $packed\n"; // Outputs packed integer value\nprint_r($unpacked); // Outputs original ratings array\n?>