<?php\n\nclass Car {\n    private $model;\n    private $year;\n    private $color;\n\n    const COLOR_RED = 0;\n    const COLOR_GREEN = 1;\n    const COLOR_BLUE = 2;\n\n    public function __construct($model, $year, $color) {\n        $this->model = $model;\n        $this->year = $year;\n        $this->color = $color;\n    }\n\n    public function displayInfo() {\n        echo "Model: {$this->model}, Year: {$this->year}, Color: ";\n        switch ($this->color) {\n            case self::COLOR_RED:\n                echo "Red";\n                break;\n            case self::COLOR_GREEN:\n                echo "Green";\n                break;\n            case self::COLOR_BLUE:\n                echo "Blue";\n                break;\n        }\n        echo "\n";\n    }\n}\n\n?>