function sendAsyncRequestWithFsockopen($url) {\n    $parsedUrl = parse_url($url);\n    \n    // Default port is 80 for HTTP\n    $port = isset($parsedUrl['port']) ? $parsedUrl['port'] : 80;\n    \n    // Open a socket to the server\n    if ($fp = fsockopen($parsedUrl['host'], $port, $errno, $errstr, 1)) {\n        fwrite($fp, "GET {$parsedUrl['path']} HTTP/1.1\r\n");\n        fwrite($fp, "Host: {$parsedUrl['host']}\r\n");\n        fwrite($fp, "Connection: Close\r\n\r\n");\n        \n        // Immediately close the socket without reading the response\n        fclose($fp);\n    }\n}\n\n// Example usage:\nsendAsyncRequestWithFsockopen("http://example.com/trigger_event.php");