function sendAsyncRequest($url) {\n    $ch = curl_init();\n    \n    // Set the URL\n    curl_setopt($ch, CURLOPT_URL, $url);\n    \n    // Return the transfer as a string (not necessary for async, but good practice)\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n    \n    // Make it asynchronous by sending the request and closing immediately\n    curl_exec($ch);\n    \n    // Close cURL resource to free up system resources\n    curl_close($ch);\n}\n\n// Example usage:\nsendAsyncRequest("http://example.com/trigger_event.php");