<?php\n\nfunction areStringsEquivalent($str1, $str2) {\n    // Create a collator for utf8 general ci equivalent comparison\n    $collator = collator_create('en_US');\n    collator_set_strength($collator, Collator::PRIMARY);\n\n    // Compare the strings using the collator\n    return collator_compare($collator, $str1, $str2) === 0;\n}\n\n// Test examples\n$a = 'a';\n$A_accented = 'À';\n\nif (areStringsEquivalent($a, $A_accented)) {\n    echo "'a' is considered equivalent to 'À' in a utf8_general_ci comparison.\n";\n} else {\n    echo "'a' and 'À' are not considered equivalent.\n";\n}\n\n?>