<?php\n\nclass A {\n    public $b;\n\n    // Implement the __clone method if you need deep cloning of properties that are objects\n    public function __clone() {\n        // If $this->b was an object, you would clone it here\n        // $this->b = clone $this->b;\n    }\n}\n\nfunction set_b($obj) { \n    $obj->b = "after"; \n}\n\n$a = new A();\n$a->b = "before";\n$c = clone $a; // This creates a copy of the object\n\nset_b($a);\n\nprint $a->b; // This will show 'after'\nprint $c->b; // This will still show 'before'\n\n?>