<?php\n\nfunction validateCreditCard($number) {\n    // Remove any non-digit characters\n    $number = preg_replace('/\D/', '', $number);\n\n    // Check if the input is empty or not numeric\n    if (empty($number)) {\n        return false;\n    }\n\n    $length = strlen($number);\n    $sum = 0;\n\n    // Process every second digit from right to left\n    for ($i = $length - 1; $i >= 0; $i -= 2) {\n        $sum += $number[$i];\n    }\n\n    // Double the remaining digits and add digits of those products\n    for ($i = $length - 2; $i >= 0; $i -= 2) {\n        $digit = $number[$i] * 2;\n        if ($digit > 9) {\n            $digit = $digit - 9;\n        }\n        $sum += $digit;\n    }\n\n    // If the total modulo 10 is not equal to 0, the number is invalid\n    return $sum % 10 === 0;\n}\n\n// Example usage:\n$cardNumber = "4532015112830366"; // Visa test number\nif (validateCreditCard($cardNumber)) {\n    echo "The credit card number is valid.\n";\n} else {\n    echo "The credit card number is invalid.\n";\n}\n?>