$filePath = 'path/to/your/file.txt';\n\n// Get the file permissions as a decimal number\n$permissions = fileperms($filePath);\n\n// Check if the file is readable, writable, and executable by the owner, group, and others\n$isReadable = is_readable($filePath);\n$isWritable = is_writable($filePath);\n$isExecutable = is_executable($filePath);\n\necho "File Permissions: " . substr(sprintf('%o', $permissions), -4) . "\n";\necho "Is Readable: " . ($isReadable ? 'Yes' : 'No') . "\n";\necho "Is Writable: " . ($isWritable ? 'Yes' : 'No') . "\n";\necho "Is Executable: " . ($isExecutable ? 'Yes' : 'No') . "\n";