function enqueue_custom_scripts() {\n    wp_enqueue_script('jquery'); // Make sure jQuery is loaded\n    wp_enqueue_script('custom-comments', get_template_directory_uri() . '/js/custom-comments.js', array('jquery'), null, true);\n    wp_localize_script('custom-comments', 'commentData', array(\n        'ajaxurl' => admin_url('admin-ajax.php'),\n        'nonce'   => wp_create_nonce('add_comment')\n    ));\n}\nadd_action('wp_enqueue_scripts', 'enqueue_custom_scripts');