public function jobdetails() {\n    $this->autoRender = false; // Ensure no view is rendered\n    if ($this->request->is('post')) {\n        $jobId = $this->request->data['id'];\n        $jobData = $this->Job->findById($jobId); // Adjust model and method as necessary\n\n        if (!empty($jobData)) {\n            echo json_encode([\n                'success' => true,\n                'jobtitle' => $jobData['Job']['jobtitle'],\n                'department' => $jobData['Job']['department']\n            ]);\n        } else {\n            echo json_encode(['success' => false]);\n        }\n    }\n}