<?php\n\n$soapUrl = "http://example.com/YourWebService.asmx"; // your webservice URL\n$xml_post_string = '<soapenv:Envelope xmlns:soapenv="http://schemas.xmlsoap.org/soap/envelope/" xmlns:tem="http://tempuri.org/">\n   <soapenv:Header/>\n   <soapenv:Body>\n      <tem:YourMethodName>\n         <!-- Assume the web service is expecting a long parameter named "longParam" -->\n         <tem:longParam>' . (string)$largeNumber . '</tem:longParam>\n      </tem:YourMethodName>\n   </soapenv:Body>\n</soapenv:Envelope>'; // XML post string (.RequestBody to SOAP server)\n\n$headers = array(\n    "Content-Type: text/xml; charset=utf-8",\n    "SOAPAction: \"http://tempuri.org/YourMethodName\"",\n    "Content-length: ".strlen($xml_post_string),\n);\n\n$url = $soapUrl;\n\n$ch = curl_init();\ncurl_setopt($ch, CURLOPT_URL, $url);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_POST, 1);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, $xml_post_string); // the SOAP request\ncurl_setopt($ch, CURLOPT_HTTPHEADER, $headers);\n\n$response = curl_exec($ch);\ncurl_close($ch);\n\necho "Response: " . $response;\n?>