$result = ImageCreateFromPNG('path/to/image.png');\nImageAlphaBlending($result, false); // Disable alpha blending\nImageSaveAlpha($result, false);    // Don't save alpha channel (JPEG does not support it)\n\n// Fill transparent areas with white\n$whiteBackground = imagecolorallocate($result, 255, 255, 255);\nimagefill($result, 0, 0, $whiteBackground);\n\nImageJPEG($result, 'path/to/output.jpg');\nImageDestroy($result);